<?php add_action( 'wp_ajax_nopriv_ajax_search_groups', 'ajax_search_groups' );add_action( 'wp_ajax_ajax_search_groups', 'ajax_search_groups' );function ajax_search_groups(){	$offset = $_POST['offset'] ? $_POST['offset'] : 0;	$limit = $_POST['limit'] ? $_POST['limit'] :10;	$url= 'https://accountsapi.logos.com/v1/accounts?q='.$_POST['search'].'&offset='.$offset.'&limit='.$limit;	//$url='https://accountsapi.logos.com/v1/accounts?q=application';	$request = wp_remote_retrieve_body( wp_remote_get( $url , array( 'timeout' => 18 , 'sslverify' => false ) ) );	echo $request;	die;}?>